import React,{Component} from 'react';
import Table from 'react-bootstrap/Table'
import Button from 'react-bootstrap/Button'
// console.log(miAuto);
class FormProduct extends Component{
    state = {
        model: {
            id: 0,
            description: '',
            price: 0,
            quantity: 0
        }
    };
    render(){
        return(
            <div>

            </div>
        );
    }
}
class ListProduct extends Component
{

    render(){

        const {products}=this.props;
        console.log(products);
           return(
               <Table>
                   <thead>
                   <tr>
                       <th>id</th>
                       <th>Nombre</th>
                   </tr>
                   </thead>
                   <tbody>
                   {
                       products.map(product=>(
                           <tr key={product.id}>
                               <td>{product.nombre}</td>
                           </tr>
                       ))
                   }
                   </tbody>
               </Table>
           )
       }

}
export default class ProductBox extends Component{

    constructor(props) {
        super(props)

        this.state = {
            products: [], // Initialize product as an empty array
        }
    }
    Url='http://localhost:3009/products';
    componentDidMount() {
        fetch(this.Url)
            .then(response => response.json())
            .then(products => this.setState({ products }))
            .catch(e => console.log(e));
    }
    render(){
        // console.log("hola->>>>",Object.values(this.state.products));

        return(
            <div className="row">
                <div className="col-md-6">
                    <h2 className="font-smaller text-center">
                        Cadastro de Productos
                    </h2>
                    <FormProduct/>
                </div>
                <div className="col-md-6">
                    <h2 className="font-smaller text-center">
                        Lista de Productos
                    </h2>
                    <ListProduct products ={this.state.products}/>
                </div>

        </div>
        );
    }
}
