import React,{Component} from 'react';
import {Table,Button} from 'reactstrap';
// console.log(miAuto);
class FormProduct extends Component{
    render(){
        return(
            <div>

            </div>
        );
    }
}
class ListProduct extends Component
{

    render(){
        const {products}=this.props;
        console.log(products);
        return(
            <Table>
                <thead>
                    <tr>
                        <th>id</th>
                        <th>Nombre</th>
                        <th>first apellido</th>
                        <th>second apellido</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        products.map(product=>(
                            <tr key={product.id_persona}>
                                <td>{product.nombre}</td>
                                <td>{product.primer_apellido}</td>
                                <td>{product.segundo_apellido}</td>
                                <td>{product.dni}</td>
                                <td>{product .email}</td>
                                <td>
                                    <Button color="info" size="sm">Edit</Button>
                                    <Button color="danger" size="sm">Delete</Button>
                                </td>
                            </tr>
                        ))
                    }
                </tbody>
            </Table>

        )
    }
}
export default class ProductBox extends Component{

    Url='http://localhost:3005/';
    state={
        products:[],
    }
    componentDidMount(){
        fetch(this.Url)
        .then(Response=>Response.json())
        .then(products=>this.setState({products}))
        .catch(e=>console.log(e));

    }

    render(){
        return(
            <div className="row">
                <div className="col-md-6">
                    <h2 className="font-smaller text-center">
                        Cadastro de Productos
                    </h2>
                    <FormProduct/>
                </div>
                <div className="col-md-6">
                    <h2 className="font-smaller text-center">
                        Lista de Productos
                    </h2>
                    <ListProduct products ={this.state.products}/>
                </div>

        </div>
        );
    }
}
