import React,{Component} from 'react';
import Table from 'react-bootstrap/Table'
import Button from 'react-bootstrap/Button'
// console.log(miAuto);
class FormProduct extends Component{

    render(){
        return(
            <div>

            </div>
        );
    }
}
class ListProduct extends Component
{

    render(){

        const { products } =  this.state = {
            products: [{
                codigo: 1,
                descripcion: 'papas',
                precio: 12.52
            },{
                codigo: 2,
                descripcion: 'naranjas',
                precio: 21
            },{
                codigo: 3,
                descripcion: 'peras',
                precio: 18.20
            }]
        };
        console.log("ListProduct");
        console.log(this.props);
        console.log("products");
        console.log(products);

        return(
            <Table>
                <thead>
                    <tr>
                        <th>id</th>
                        <th>Nombre</th>
                    </tr>
                </thead>
                <tbody>
                {
                    products.map(product => (
                        <tr key={product.codigo}>
                            <td>{product.descripcion}</td>
                            <td>{product.precio}</td>
                            <td>
                                <Button color="info" size="sm" onClick={e => this.onEdit(product)}>Editar</Button>
                                <Button color="danger" size="sm" onClick={e => this.delete(product.id)}>Deletar</Button>
                            </td>
                        </tr>
                    ))


                }

                </tbody>
            </Table>
        )
    }
}
export default class ProductBox extends Component{

    Url='http://localhost:3009/products';
    state={
        products:[],
    }
    componentDidMount() {
        fetch(this.Url)
            .then(response => response.json())
            .then(products => this.setState({ products }))
            .catch(e => console.log(e));
    }

    render(){
        return(
            <div className="row">
                <div className="col-md-6">
                    <h2 className="font-smaller text-center">
                        Cadastro de Productos
                    </h2>
                    <FormProduct/>
                </div>
                <div className="col-md-6">
                    <h2 className="font-smaller text-center">
                        Lista de Productos
                    </h2>
                    <ListProduct products ={this.state.products}/>
                </div>

        </div>
        );
    }
}
