import React,{Component} from 'react';
import Table from 'react-bootstrap/Table'
import Button from 'react-bootstrap/Button'
// console.log(miAuto);
class FormProduct extends Component{
    render(){
        return(
            <div>

            </div>
        );
    }
}
class ListProduct extends Component
{
    state={
        products:[]
    }

    render(){
        return(
            <Table>
                <thead>
                <tr>
                    <th>id</th>
                    <th>Nombre</th>
                </tr>
                </thead>
                <tbody>
                {
                    this.state.products.map(post=>{
                        return(
                            <div key={post.id}>
                                <h1>{post.nombre}</h1>

                            </div>
                        )
                    })
                }
                </tbody>
            </Table>
        )
    }

}
export default class ProductBox extends Component{

    state={
        products:[]
    }

    async componentDidMount() {
        const res = await fetch('http://localhost:3009/products');
        const data= await res.json();
        console.log(data);
        this.setState({products:data})
        console.log(this.setState({products:data}));

    }
    render(){
        // console.log("hola->>>>",Object.values(this.state.products));

        return(
            <div className="row">
                <div className="col-md-6">
                    <h2 className="font-smaller text-center">
                        Cadastro de Productos
                    </h2>
                    <FormProduct/>
                </div>
                <div className="col-md-6">
                    <h2 className="font-smaller text-center">
                        Lista de Productos
                    </h2>
                    {
                        this.state.products.map(products=>{
                            return(
                                <div key={products.id}>
                                    <h1>{products.nombre}</h1>

                                </div>
                            )
                        })
                    }
                    {/*<ListProduct products ={this.state.products}/>*/}
                </div>

            </div>
        );
    }
}
