export default class ProductBox extends Component{

    state={
        products:[]
    }
    handleSubmit = event => {
        event.preventDefault();
        const lastPost = this.state.posts.slice(-1).pop();
        let newPostObj = {
            id: lastPost ? lastPost.id + 1 : 0,
            title: event.target.title.value,
            body: event.target.body.value
        };

        fetch("https://jsonplaceholder.typicode.com/posts", {
            method: "POST",
            body: JSON.stringify(newPostObj),
            headers: {
                "Content-type": "application/json; charset=UTF-8"
            }
        })
            .then(response => response.json())
            .then(newPost => {
                const posts = [...this.state.posts, newPost];
                this.setState({ posts: posts });
            });
    };
    render(){
        // console.log("hola->>>>",Object.values(this.state.products));
        console.log(this.products);
        return(
            <div className="row">
                <div className="col-md-6">
                    <h2 className="font-smaller text-center">
                        Cadastro de Productos
                    </h2>
                    <FormProduct/>
                </div>
                <div className="col-md-6">
                    <h2 className="font-smaller text-center">
                        Lista de Productos
                    </h2>
                    <input type="button" onClick={this.handleSubmit}> enviar</input>
                </div>

            </div>
        );
    }
}
