import React,{Component} from 'react';
import Table from 'react-bootstrap/Table'
import Button from 'react-bootstrap/Button'
// console.log(miAuto);
class FormProduct extends Component{

    render(){
        return(
            <div>

            </div>
        );
    }
}
class ListProduct extends Component
{
    render(){
        return(
            <Table>
                <thead>
                    <tr>
                        <th>id</th>
                        <th>Nombre</th>
                    </tr>
                </thead>
                <tbody>
                {this.state.products.map(elemento => {
                    return (
                        <tr key={elemento.codigo}>
                            <td>
                                {elemento.codigo}
                            </td>
                            <td>
                                {elemento.descripcion}
                            </td>
                            <td>
                                {elemento.precio}
                            </td>
                            <td>
                                <button onClick={()=>this.borrar(elemento.codigo)}>Borrar</button>
                            </td>
                        </tr>
                    )
                })}

                </tbody>
            </Table>
        )
    }
}
export default class ProductBox extends Component{

    Url='http://localhost:3009/products';
    constructor(props) {
        super(props)
        this.state={
            products:[]
        }
    }



    componentDidMount() {
        fetch(this.Url)
            .then(response => response.json())
            .then(products => this.setState({ products }))
            .catch(e => console.log(e));
    }

    render(){
        return(
            <div className="row">
                <div className="col-md-6">
                    <h2 className="font-smaller text-center">
                        Cadastro de Productos
                    </h2>
                    <FormProduct/>
                </div>
                <div className="col-md-6">
                    <h2 className="font-smaller text-center">
                        Lista de Productos
                    </h2>
                    <ListProduct products ={this.state.products}/>
                </div>

        </div>
        );
    }
}
