                let product =res.data.notas
                this.setState({
                    products:product
                });
            })
        .catch(err=>{
            console.log(err);
        })
    }


    render(){
        // console.log("hola->>>>",Object.values(this.state.products));
        console.log(this.products);
        return(
            <div className="row">
                <div className="col-md-6">
                    <h2 className="font-smaller text-center">
                        Cadastro de Productos
                    </h2>
                    <FormProduct/>
                </div>
                <div className="col-md-6">
                    <h2 className="font-smaller text-center">
                        Lista de Productos
                    </h2>
                    ProductBox
                    {
                        this.state.products.map(products=>{
                            return(
                                <div key={products.id}>
                                    <h1>{products.nombre}</h1>

                                </div>
                            )
                        })
                    }
                    {/*<ListProduct products ={this.state.products}/>*/}
                </div>

            </div>
        );
    }
}
