import React,{Component} from 'react';
var miAuto = new Object();
miAuto.marca = "Ford";
miAuto.modelo = "Mustang";
miAuto.año = 1969;
// console.log(miAuto);
class FormProduct extends Component{
    render(){
        return(
            <div>
                <h2 className="font-smaller text-center">
                    Cadastro de Productos
                </h2>
            </div>
        );
    }
}
class ListProduct extends Component
{
    render(){
        return(
            <div>
                <h2 className="font-smaller text-center">
                    Lista de Productos
                </h2>
            </div>
        )
    }
}
export default class ProductBox extends Component{

    state={
        products:[],
    }
    componentDidMount(){
        const express = require('express')
        const app = express()
        const port = 3003
        app.use(function(req, res, next) {
            res.header("Access-Control-Allow-Origin", "*");
            res.header("Access-Control-Allow-Headers", "Origin, X-Requested-With, Content-Type, Accept, Access-Control-Allow-Origin, Cache-Control");
            // res.json({data: [1,2,3,4]})
            next()
          });
        app.post('/api/v1/login', (req, res) =>   {

            res.json({data: [1,2,3,4]})
            next()
        })

        app.listen(port, () => console.log(`Example app listening on port ${port}!`))

    }



    render(){
        return(
            <div className="row">
                <div className="col-md-6">
                    <FormProduct/>
                </div>
                <div className="col-md-6">
                    <ListProduct/>
                </div>

        </div>
        );
    }
}
